import 'package:cached_network_image/cached_network_image.dart';
import 'package:flutter/material.dart';
import 'package:get/get.dart';
import 'package:shimmer/shimmer.dart';
import 'package:flutter_screenutil/flutter_screenutil.dart';

import '../controllers/filters_display_controller.dart';
import 'FilteredProductsScreen.dart';

/// ويدجت لعرض جميع الفلاتر بدلاً من المنتجات
class FiltersGridWidget extends StatelessWidget {
  const FiltersGridWidget({super.key});

  @override
  Widget build(BuildContext context) {
    final controller = Get.put(FiltersDisplayController());
    final theme = Theme.of(context);

    return Obx(() {
      if (controller.isLoading.value) {
        return _buildLoadingGrid(context);
      }

      if (controller.allFilters.isEmpty) {
        return _buildEmptyState(context, theme);
      }

      return Padding(
        padding: EdgeInsets.symmetric(horizontal: 7.w),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            // عنوان القسم
            Container(
              padding: EdgeInsets.symmetric(horizontal: 16.w, vertical: 12.h),
              margin: EdgeInsets.only(bottom: 16.h),
              decoration: BoxDecoration(
                gradient: LinearGradient(
                  colors: [
                    theme.primaryColor.withOpacity(0.1),
                    theme.primaryColor.withOpacity(0.05),
                  ],
                  begin: Alignment.centerLeft,
                  end: Alignment.centerRight,
                ),
                borderRadius: BorderRadius.circular(16.r),
                border: Border.all(
                  color: theme.primaryColor.withOpacity(0.2),
                  width: 1.w,
                ),
              ),
              child: Row(
                children: [
                  Container(
                    width: 40.w,
                    height: 40.h,
                    decoration: BoxDecoration(
                      color: theme.primaryColor,
                      borderRadius: BorderRadius.circular(12.r),
                      boxShadow: [
                        BoxShadow(
                          color: theme.primaryColor.withOpacity(0.3),
                          blurRadius: 8.r,
                          offset: Offset(0, 4.h),
                        ),
                      ],
                    ),
                    child: Icon(
                      Icons.tune_rounded,
                      color: Colors.white,
                      size: 22.sp,
                    ),
                  ),
                  SizedBox(width: 12.w),
                  Expanded(
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Text(
                          'تصفح حسب الفئات',
                          style: TextStyle(
                            fontSize: 18.sp,
                            fontWeight: FontWeight.bold,
                            color: theme.primaryColor,
                          ),
                        ),
                        Text(
                          '${controller.allFilters.length} فلتر متاح',
                          style: TextStyle(
                            fontSize: 14.sp,
                            color: theme.textTheme.bodySmall?.color
                                ?.withOpacity(0.7),
                          ),
                        ),
                      ],
                    ),
                  ),
                  IconButton(
                    onPressed: () => controller.refreshFilters(),
                    icon: Icon(
                      Icons.refresh_rounded,
                      color: theme.primaryColor,
                      size: 20.sp,
                    ),
                    tooltip: 'إعادة تحميل',
                  ),
                ],
              ),
            ),

            // شبكة الفلاتر
            GridView.builder(
              physics: const NeverScrollableScrollPhysics(),
              shrinkWrap: true,
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                childAspectRatio: 0.9,
                crossAxisSpacing: 10.w,
                mainAxisSpacing: 10.h,
              ),
              itemCount: controller.allFilters.length,
              itemBuilder: (context, index) {
                final filter = controller.allFilters[index];
                return _buildFilterCard(context, filter, theme);
              },
            ),
          ],
        ),
      );
    });
  }

  /// بناء بطاقة الفلتر
  Widget _buildFilterCard(
    BuildContext context,
    FilterItemModel filter,
    ThemeData theme,
  ) {
    return Card(
      elevation: 3,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(16.r)),
      clipBehavior: Clip.antiAlias,
      child: InkWell(
        onTap: () => _navigateToFilteredProducts(filter),
        borderRadius: BorderRadius.circular(16.r),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.stretch,
          children: [
            // منطقة الصورة
            Expanded(
              flex: 3,
              child: Stack(
                fit: StackFit.expand,
                children: [
                  // الصورة
                  _buildFilterImage(filter),

                  // شريط نوع الفلتر
                  Positioned(
                    top: 8.h,
                    right: 8.w,
                    child: Container(
                      padding: EdgeInsets.symmetric(
                        horizontal: 6.w,
                        vertical: 3.h,
                      ),
                      decoration: BoxDecoration(
                        color: filter.type.color.withOpacity(0.9),
                        borderRadius: BorderRadius.circular(8.r),
                        boxShadow: [
                          BoxShadow(
                            color: Colors.black.withOpacity(0.2),
                            blurRadius: 4.r,
                            offset: Offset(0, 2.h),
                          ),
                        ],
                      ),
                      child: Row(
                        mainAxisSize: MainAxisSize.min,
                        children: [
                          Icon(
                            filter.type.icon,
                            color: Colors.white,
                            size: 12.sp,
                          ),
                          SizedBox(width: 3.w),
                          Text(
                            filter.type.displayName,
                            style: TextStyle(
                              color: Colors.white,
                              fontSize: 9.sp,
                              fontWeight: FontWeight.bold,
                            ),
                          ),
                        ],
                      ),
                    ),
                  ),
                ],
              ),
            ),

            // منطقة النص
            Expanded(
              flex: 2,
              child: Padding(
                padding: EdgeInsets.all(10.w),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    // العنوان الرئيسي
                    Text(
                      filter.title,
                      style: TextStyle(
                        fontSize: 13.sp,
                        fontWeight: FontWeight.bold,
                        color: theme.textTheme.titleMedium?.color,
                      ),
                      maxLines: 1,
                      overflow: TextOverflow.ellipsis,
                    ),
                    SizedBox(height: 3.h),

                    // العنوان الفرعي
                    Text(
                      filter.subtitle,
                      style: TextStyle(
                        fontSize: 11.sp,
                        color: theme.textTheme.bodySmall?.color?.withOpacity(
                          0.7,
                        ),
                        fontWeight: FontWeight.w500,
                      ),
                      maxLines: 1,
                      overflow: TextOverflow.ellipsis,
                    ),

                    // اسم الوالد إن وجد
                    if (filter.parentName != null) ...[
                      SizedBox(height: 2.h),
                      Text(
                        filter.parentName!,
                        style: TextStyle(
                          fontSize: 9.sp,
                          color: filter.type.color,
                          fontWeight: FontWeight.w600,
                        ),
                        maxLines: 1,
                        overflow: TextOverflow.ellipsis,
                      ),
                    ],

                    // عدد المنتجات إن وجد
                    if (filter.productCount != null) ...[
                      SizedBox(height: 2.h),
                      Container(
                        padding: EdgeInsets.symmetric(
                          horizontal: 6.w,
                          vertical: 2.h,
                        ),
                        decoration: BoxDecoration(
                          color: filter.type.color.withOpacity(0.1),
                          borderRadius: BorderRadius.circular(8.r),
                          border: Border.all(
                            color: filter.type.color.withOpacity(0.3),
                            width: 0.5.w,
                          ),
                        ),
                        child: Text(
                          '${filter.productCount} منتج',
                          style: TextStyle(
                            fontSize: 8.sp,
                            color: filter.type.color,
                            fontWeight: FontWeight.bold,
                          ),
                        ),
                      ),
                    ],
                  ],
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }

  /// بناء صورة الفلتر
  Widget _buildFilterImage(FilterItemModel filter) {
    return filter.imageUrl != null && filter.imageUrl!.isNotEmpty
        ? CachedNetworkImage(
          imageUrl: filter.imageUrl!,
          fit: BoxFit.cover,
          placeholder: (context, url) => _buildImagePlaceholder(filter),
          errorWidget: (context, url, error) => _buildImagePlaceholder(filter),
        )
        : _buildImagePlaceholder(filter);
  }

  /// بناء placeholder للصورة
  Widget _buildImagePlaceholder(FilterItemModel filter) {
    return Container(
      decoration: BoxDecoration(
        gradient: LinearGradient(
          colors: [
            filter.type.color.withOpacity(0.2),
            filter.type.color.withOpacity(0.1),
          ],
          begin: Alignment.topLeft,
          end: Alignment.bottomRight,
        ),
      ),
      child: Center(
        child: Icon(
          filter.type.icon,
          size: 40.sp,
          color: filter.type.color.withOpacity(0.6),
        ),
      ),
    );
  }

  /// الانتقال إلى صفحة المنتجات المفلترة
  void _navigateToFilteredProducts(FilterItemModel filter) {
    Get.to(
      () => FilteredProductsScreen(
        filterKey: filter.filterKey,
        filterTitle: filter.title,
        filterSubtitle: filter.subtitle,
        filterType: filter.type,
      ),
      transition: Transition.rightToLeft,
      duration: const Duration(milliseconds: 300),
    );
  }

  /// بناء شبكة التحميل
  Widget _buildLoadingGrid(BuildContext context) {
    return Padding(
      padding: EdgeInsets.symmetric(horizontal: 7.w),
      child: Column(
        children: [
          // placeholder للعنوان
          Container(
            height: 64.h,
            margin: EdgeInsets.only(bottom: 16.h),
            decoration: BoxDecoration(
              color: Colors.grey[200],
              borderRadius: BorderRadius.circular(16.r),
            ),
          ),

          // شبكة placeholder
          Shimmer.fromColors(
            baseColor: Colors.grey[300]!,
            highlightColor: Colors.grey[100]!,
            child: GridView.builder(
              physics: const NeverScrollableScrollPhysics(),
              shrinkWrap: true,
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                childAspectRatio: 0.9,
                crossAxisSpacing: 10.w,
                mainAxisSpacing: 10.h,
              ),
              itemCount: 6,
              itemBuilder: (context, index) => _buildLoadingCard(),
            ),
          ),
        ],
      ),
    );
  }

  /// بناء بطاقة تحميل
  Widget _buildLoadingCard() {
    return Card(
      elevation: 3,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(16.r)),
      child: Column(
        children: [
          Expanded(
            flex: 3,
            child: Container(
              decoration: BoxDecoration(
                color: Colors.white,
                borderRadius: BorderRadius.vertical(top: Radius.circular(16.r)),
              ),
            ),
          ),
          Expanded(
            flex: 2,
            child: Padding(
              padding: EdgeInsets.all(10.w),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Container(height: 14.h, color: Colors.white),
                  SizedBox(height: 6.h),
                  Container(height: 10.h, width: 80.w, color: Colors.white),
                ],
              ),
            ),
          ),
        ],
      ),
    );
  }

  /// بناء حالة فارغة
  Widget _buildEmptyState(BuildContext context, ThemeData theme) {
    return Center(
      child: Padding(
        padding: EdgeInsets.all(32.w),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Icon(
              Icons.filter_alt_off_outlined,
              size: 60.sp,
              color: Colors.grey[400],
            ),
            SizedBox(height: 16.h),
            Text(
              'لا توجد فلاتر متاحة',
              style: TextStyle(
                fontSize: 16.sp,
                fontWeight: FontWeight.bold,
                color: Colors.grey[600],
              ),
            ),
            SizedBox(height: 8.h),
            Text(
              'تأكد من وجود أقسام وشركات مفعلة',
              style: TextStyle(fontSize: 14.sp, color: Colors.grey[500]),
              textAlign: TextAlign.center,
            ),
            SizedBox(height: 24.h),
            ElevatedButton.icon(
              onPressed:
                  () => Get.find<FiltersDisplayController>().refreshFilters(),
              icon: const Icon(Icons.refresh_rounded),
              label: const Text('إعادة المحاولة'),
              style: ElevatedButton.styleFrom(
                backgroundColor: theme.primaryColor,
                foregroundColor: Colors.white,
                padding: EdgeInsets.symmetric(horizontal: 20.w, vertical: 10.h),
                shape: RoundedRectangleBorder(
                  borderRadius: BorderRadius.circular(12.r),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
