import 'package:flutter/material.dart';
import 'package:get/get.dart';
import 'dart:typed_data';

import 'code_phone_page.dart';

/// صفحة تجريبية لاختبار نظام التحقق من الهاتف الجديد
class TestCodePhonePage extends StatelessWidget {
  const TestCodePhonePage({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('اختبار نظام التحقق الجديد'),
        backgroundColor: Colors.blue.shade600,
        foregroundColor: Colors.white,
      ),
      body: Padding(
        padding: const EdgeInsets.all(24.0),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Icon(Icons.phone_android, size: 100, color: Colors.blue.shade600),
            const SizedBox(height: 24),
            const Text(
              'نظام التحقق من الهاتف المحسن',
              style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
              textAlign: TextAlign.center,
            ),
            const SizedBox(height: 16),
            Text(
              'تم إنشاء نظام متطور للتحقق من رقم الهاتف مع:\n\n'
              '✅ واجهة مستخدم احترافية وجميلة\n'
              '✅ نظام تتبع الأخطاء المتقدم\n'
              '✅ تحقق متقدم من الرمز\n'
              '✅ تحديثات فورية للحالة\n'
              '✅ تصميم متجاوب وسلس\n'
              '✅ معالجة شاملة للأخطاء',
              style: TextStyle(
                fontSize: 16,
                color: Colors.grey.shade700,
                height: 1.6,
              ),
              textAlign: TextAlign.center,
            ),
            const SizedBox(height: 40),
            ElevatedButton.icon(
              onPressed: () => _testCodePhonePage(),
              icon: const Icon(Icons.play_arrow),
              label: const Text(
                'اختبار الصفحة الجديدة',
                style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
              ),
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.blue.shade600,
                foregroundColor: Colors.white,
                padding: const EdgeInsets.symmetric(
                  horizontal: 32,
                  vertical: 16,
                ),
                shape: RoundedRectangleBorder(
                  borderRadius: BorderRadius.circular(12),
                ),
                elevation: 3,
              ),
            ),
            const SizedBox(height: 20),
            Card(
              color: Colors.green.shade50,
              elevation: 2,
              shape: RoundedRectangleBorder(
                borderRadius: BorderRadius.circular(12),
                side: BorderSide(color: Colors.green.shade200),
              ),
              child: Padding(
                padding: const EdgeInsets.all(16),
                child: Column(
                  children: [
                    Icon(
                      Icons.check_circle,
                      color: Colors.green.shade600,
                      size: 32,
                    ),
                    const SizedBox(height: 8),
                    Text(
                      'تم حل مشكلة reCAPTCHA بنجاح!',
                      style: TextStyle(
                        color: Colors.green.shade800,
                        fontWeight: FontWeight.bold,
                        fontSize: 16,
                      ),
                    ),
                    const SizedBox(height: 4),
                    Text(
                      'النظام الآن يعمل بطريقة احترافية ومتطورة',
                      style: TextStyle(
                        color: Colors.green.shade700,
                        fontSize: 14,
                      ),
                    ),
                  ],
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }

  void _testCodePhonePage() {
    // إنشاء بيانات تجريبية
    final testImage = Uint8List.fromList([
      // بيانات تجريبية لصورة صغيرة
      255, 255, 255, 255, 255, 255, 255, 255,
      255, 255, 255, 255, 255, 255, 255, 255,
    ]);

    Get.to(
      () => CodePhonePage(
        phoneNumber: '+966501234567',
        userImage: testImage,
        name: 'مستخدم تجريبي',
        email: 'test@example.com',
        password: 'password123',
        hasPassword: true,
      ),
    );
  }
}
